mapper.select:
select * from (select row_number()over(order by compDateStart) as rnum, compSeq,compTitle,compContent,compLocation,compDateStart,compDateEnd ,compLocal,locationLat,locationLng,receiptStart,receiptEnd,compimage,compLink,compSponsor,readcount ,(select count(*) from compComment where compSeq=c.compSeq)as commentcount from competition c where 1=1
and compTitle like concat('%',#{search},'%')
and compContent like concat('%',#{search},'%')
and compLocal=#{search}
order by compDateStart asc) a where rnum between ${start} and ${end}

     SELECT * FROM competition
		WHERE YEAR(compDateStart) = YEAR(CURRENT_TIMESTAMP)
		AND MONTH(compDateStart) = MONTH(CURRENT_TIMESTAMP);
